import React from 'react';
import { Handle, Position } from 'react-flow-renderer';

export default function DecisionNode({ data }) {
  return (
    <div style={{
      width: '100px',
      height: '100px',
      transform: 'rotate(45deg)',
      background: '#f0f4c3',
      border: '2px solid #333',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      position: 'relative',
    }}>
      {/* Incoming edge from top */}
      <Handle 
        type="target" 
        position={Position.Top} 
        style={{ background: '#555' }} 
      />

      {/* BOTTOM blue circle (visually appears at bottom point) */}
      <Handle 
        type="source" 
        position={Position.Right} 
        id="bottom" 
        style={{ 
          background: '#2196F3', 
          top: '50%', 
          left: '100%', 
          transform: 'translateY(-50%)',
          width: '12px',
          height: '12px',
          border: '2px solid #2196F3'
        }} 
      />

      {/* RIGHT blue circle (visually appears at right point) */}
      <Handle 
        type="source" 
        position={Position.Bottom} 
        id="right" 
        style={{ 
          background: '#2196F3', 
          top: '100%', 
          left: '50%', 
          transform: 'translateX(-50%)',
          width: '12px',
          height: '12px',
          border: '2px solid #2196F3'
        }} 
      />


      <div style={{ transform: 'rotate(-45deg)', textAlign: 'center', fontFamily: 'monospace' }}>
        {data.label}
      </div>
    </div>
  );
}
