import React, { useState } from 'react';
import ReactFlow from 'react-flow-renderer';
import 'react-flow-renderer/dist/style.css';
import AssignmentNode from './AssignmentNode';
import IoNode from './IoNode';
import DecisionNode from './DecisionNode';

const nodeTypes = {
  assignment: AssignmentNode,
  io: IoNode,
  decision: DecisionNode,
};

export default function App() {
  const [code, setCode] = useState('');
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);

  const handleSubmit = async () => {
    const response = await fetch("http://localhost:8000/parse/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code }),
    });
    const data = await response.json();
    const endNodeId = `end`;
    const lastNode = data.nodes[data.nodes.length - 1];
    const endNode = {
    id: endNodeId,
    type: 'assignment',
    data: { label: 'END' },
    position: { x: window.innerWidth / 2 - 100, y: data.nodes.length * 150 },
    sourcePosition: 'bottom',
    targetPosition: 'top'
    };

    const endEdge = {
    id: `e${lastNode.id}-${endNodeId}`,
    source: lastNode.id,
    target: endNodeId,
    markerEnd: { type: 'arrowclosed', color: '#000' },
    style: { stroke: '#000', strokeWidth: 2 }
    };

    setNodes(data.nodes.map((n, i) => ({
        ...n,
        position: { x: window.innerWidth / 2 - 100, y: i * 150 },
        sourcePosition: 'bottom',
        targetPosition: 'top'
      })));
    setEdges([...data.edges, endEdge]);
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h1>Python AST Flowchart Visualizer</h1>
      <textarea
        rows={10}
        cols={80}
        value={code}
        onChange={(e) => setCode(e.target.value)}
        placeholder="Paste Python code..."
        style={{ width: '100%', fontFamily: 'monospace' }}
      />
      <br />
      <button onClick={handleSubmit}>Visualize</button>

      <div style={{ height: '600px', border: '1px solid #ccc', marginTop: '1rem' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        nodeTypes={nodeTypes}
        fitView
        panOnDrag
        zoomOnScroll
        zoomOnPinch
        defaultEdgeOptions={{
            type: 'step',
            markerEnd: {
            type: 'arrowclosed',
            color: '#000',
            },
            style: { stroke: '#000', strokeWidth: 2 }
        }}
        />
      </div>
    </div>
  );
}
