import React from 'react';
import { Handle, Position } from 'react-flow-renderer';

export default function DecisionNode({ data }) {
  return (
    <div style={{
      width: '100px',
      height: '100px',
      transform: 'rotate(45deg)',
      background: '#f0f4c3',
      border: '2px solid #333',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      position: 'relative',
    }}>
      {/* Incoming edge from top */}
      <Handle type="target" position={Position.Top} style={{ background: '#555' }} />

      {/* YES edge comes from the bottom edge */}
      <Handle type="source" position={Position.Bottom} id="yes" style={{ background: 'green', top: '100%', left: '50%', transform: 'translateX(-50%)' }} />

      {/* NO edge comes from the right edge */}
      <Handle type="source" position={Position.Right} id="no" style={{ background: 'red', top: '50%', left: '100%', transform: 'translateY(-50%)' }} />

      <div style={{ transform: 'rotate(-45deg)', textAlign: 'center', fontFamily: 'monospace' }}>
        {data.label}
      </div>
    </div>
  );
}
